function sym(args) {
  //iterate through each array
  //add to new arr if not in new arr

  let arr_arguments = Array.prototype.slice.call(arguments);

  function union(s1, s2) {
    let s3 = s1.concat(s2);
    let unionSet = [];

    for (let i = 0; i < s3.length; i += 1) {
      if (!unionSet.includes(s3[i])) {
        unionSet.push(s3[i]);
      };
    };

    return unionSet;
  };

  function difference(s1, s2) {
    let unionSet = union(s1, s2);
    let differenceSet = [];

    unionSet.forEach(function (element) {
      if (!(s1.includes(element) && s2.includes(element))) {
        differenceSet.push(element)
      };
    });

    return differenceSet;
  };

  if (arr_arguments.length == 2) {
    return difference(arr_arguments[0], arr_arguments[1]);
  } else {
    let differenceSet = difference(arr_arguments[0], arr_arguments[1]);

    for (let i = 2; i < arr_arguments.length; i += 1) {
      differenceSet = difference(differenceSet, arr_arguments[i])
    };

    return differenceSet;
  };

};

sym([1, 2, 3], [5, 2, 1, 4]);
