function mergeSort(arr) {
  if (arr.length == 0) {     
    return [];   
  };    

  let idxMiddle = Math.floor(arr.length / 2);   
  let arrLeft = arr.slice(0, idxMiddle);   
  let arrRight = arr.slice(idxMiddle, arr.length);    

  if ((arrLeft.length == 0 || arrRight.length == 0)) {     
    return arrLeft.concat(arrRight)   
  };    

  arrLeft = mergeSort(arrLeft);   
  arrRight = mergeSort(arrRight);    
  let combined = [];    

  while ((arrLeft.length != 0 && arrRight.length != 0)) {     
    if (arrLeft[0] < arrRight[0]) {       
      combined.push(arrLeft.shift());     
    } else {       
      combined.push(arrRight.shift())     
    };   
  };    

  return combined.concat(arrLeft.concat(arrRight)); 
}  

