function quickSort(arr) {

  if (arr.length == 0) {
    return arr;
  };

  let arrCopy = arr.slice();
  console.log(arrCopy);

  let arrLess = [];
  let arrGreat = [];

  let pivot = []
  pivot.push(arrCopy.pop());
  console.log(pivot);
  console.log(arrCopy);

  for (let i = 0; i < arrCopy.length; i += 1) {
    if (arrCopy[i] < pivot[0]) {
      arrLess.push(arrCopy[i]);
    } else {
      arrGreat.push(arrCopy[i]);
    };
  };

  console.log(arrLess);
  console.log(arrGreat);

  if (arrLess.length <= 1 & arrGreat.length <= 1) {
    return arrLess.concat(pivot).concat(arrGreat);
  };

  arrLess = quickSort(arrLess);
  arrGreat = quickSort(arrGreat);

  return arrLess.concat(pivot).concat(arrGreat);
}

