function updateInventory(arr1, arr2) {
    // All inventory must be accounted for or you're fired!
    let arr = [];
    arr1.forEach(function(element) {
        arr.push(element[1]);
    });
    arr2.forEach(function(element) {
        if (!arr.includes(element[1])) {
            arr.push(element[1]);
        };
    });
    arr = arr.sort().map(element => [0, element])
    console.log(arr);

    let inventory = arr1.concat(arr2);

    for (let i = 0; i < arr.length; i += 1) {
        for (let j = 0; j < inventory.length; j += 1) {
            if (inventory[j].includes(arr[i][1])) {
                arr[i][0] += inventory[j][0];
            };
        };
    };
    return arr;
};

// Example inventory lists
var curInv = [
    [21, "Bowling Ball"],
