function pairwise(arr, arg) {
  if (arr.length == 0) {
    return 0;
  };

  let indices = [];
  let pairs = [];

  for (let i = 0; i < arr.length; i += 1) {
    for (let j = 0; j < arr.length; j += 1) {
      if (i != j) {
        if (arr[i] + arr[j] == arg) {
          if (!(indices.includes(i) || indices.includes(j))) {
            indices.push(i);
            indices.push(j);
          };
          if (!(pairs.includes(arr[i]) || pairs.includes(arr[j]))) {
            pairs.push(arr[i]);
            pairs.push(arr[j]);
          };
        };
      };
    };
  };

  console.log(indices);
  console.log(pairs);
  return indices.reduce((a, b) => a + b);
};

