function binaryAgent(str) {
  let arr = str.split(' ')

  let finalStr = "";
  for (let i = 0; i < arr.length; i += 1) {
    finalStr += String.fromCharCode(convertBinary(arr[i]));
  };

  return finalStr;
}

function convertBinary(str) {
  let binaryDecimal = [128, 64, 32, 16, 8, 4, 2, 1];

  let decimal = 0;
  for (let i = 0; i < str.length; i += 1) {
    if (str[i] == 1) {
      decimal += binaryDecimal[i];
    };
  };

  return decimal;
}

