function convertHTML(str) {

  let first = /&/gi;
  let second = /</gi;
  let third = />/gi;
  let fourth = /"/gi;
  let fifth = /'/gi;

  str = str.replace(first, '&amp;')
  str = str.replace(second, '&lt;')
  str = str.replace(third, '&gt;')
  str = str.replace(fourth, '&quot;')
  str = str.replace(fifth, "&apos;")


  return str;
}

convertHTML("Dolce & Gabbana");
