function dropElements(arr, func) {
  // Drop them elements.
  //forEach
  //find true
  //slice array and return


  let new_arr = [];

  for (let i = 0; i < arr.length; i += 1) {
    if (func(arr[i]) == true) {
      new_arr = arr.slice(i);
      break;
    };
  };

  return new_arr;
};

dropElements([1, 2, 3], function(n) {return n < 3; });
