function translatePigLatin(str) {
  let vowelsStart = /^[aeiou]/

  if (vowelsStart.test(str)) {
    return str + "way";
  };

  let vowels = /[aeiou]/gi

  if (!vowels.test(str)) {
    return str + 'ay';
  };

  let consonants = /[^aeiou]+/
  let replacement = str.match(consonants)[0];
  return str.replace(replacement, "") + replacement + "ay"
}

translatePigLatin("consonant");
