function uniteUnique(arr) {
  console.log(arguments);
  console.log(arr);
  let new_arr = arr.slice();
  console.log(new_arr);

  for (let i = 1; i < arguments.length; i += 1) {
    arguments[i].forEach(function(number) {
      if (!new_arr.includes(number)) {
        new_arr.push(number);
      }
    })
  }

  console.log(new_arr);
  return new_arr;
}

uniteUnique([1, 3, 2], [5, 2, 1, 4], [2, 1]);
