function steamrollArray(arr) {
  let new_arr = []
  arr.forEach(function (element) {
    if (Array.isArray(element)) {
      if (element.length == 1) {
        new_arr.push(flatten_it(element));
      } else {
        new_arr = new_arr.concat(steamrollArray(element));
      }
    } else {
      new_arr.push(element);
    };
  });

  return new_arr;
};

function flatten_it (arr) {
  let new_arr;
  if (Array.isArray(arr[0])) {
    new_arr = flatten_it(arr[0]);
  } else {
    return arr[0];
  };

  return new_arr;
};

steamrollArray([1, [2], [3, [[4]]]]);
