function sumFibs(num) {
  if (num == 1) {
    return 1;
  }

  let arr = [1, 1];

  while (arr[arr.length - 1] <= num) {
    let sequence = arr[arr.length - 1] + arr[arr.length - 2];
      arr.push(sequence);
  };
  console.log(arr)
  return arr.filter(element => element % 2 != 0).reduce((a,b) => a + b) - arr[arr.length - 1];
}

function fibs(num) {
  if (num == 1) {
    return [1];
  }
  if (num == 2) {
    return [1, 1];
  }
  let arr = fibs(num - 1);

  return arr.concat(arr[arr.length - 1] + arr[arr.length - 2]); 
}

