function sumPrimes(num) {
  let sum = 0;

  for (let i = 2; i <= num; i += 1) {
    if (prime(i)) {
      sum += i;
    };
  };

  return sum;
}

function prime(n) {
  if (n <= 1) {
    return false;
  } else {
    for (let i = 2; i < n; i += 1) {
      if (n % i == 0) {
        return false;
      };
    };
    return true;
  };
}

sumPrimes(10);
