function palindrome(str) {
  // Good luck!
  let new_str = str.replace(/[^a-zA-Z0-9]/ig, " ");
  console.log(new_str);

  let arr = new_str.match(/\S+/gi);
  console.log(arr);

  new_str = arr.join("").toLowerCase();
  let reversed = new_str.split('').reverse().join('');
  console.log(new_str);
  console.log(reversed);

  return reversed == new_str;
}



palindrome("eye");
palindrome("_eye");
palindrome("A man, a plan, a canal. Panama");
