function convertToRoman(num) {
    let reference = { 1 : "I", 2 : "II", 3 : "III", 4 : "IV", 5 : "V", 6 : "VI", 7 : "VII", 8 : "VIII", 9: "IX", 10 : "X", 20 : "XX", 30 : "XXX", 40 : "XL", 50 : "L", 60 : "LX", 70 : "LXX", 80 : "LXXX", 90 : "XC", 100 : "C", 200 : "CC", 300 : "CCC", 400 : "CD", 500 : "D", 600 : "DC", 700 : "DCC", 800 : "DCCC", 900 : "CM", 1000 : "M", 2000 : "MM", 3000 : "MMM" };

    let onePlace = num % 10;
    let tenPlace = Math.floor(num % 100 / 10) * 10;
    let hundredPlace = Math.floor(num % 1000 / 100) * 100;
    let thousandPlace = Math.floor(num % 10000 / 1000) * 1000;

    let convertedNum = "";
    if (reference[thousandPlace] != undefined) {
        convertedNum += reference[thousandPlace];
    };
    if (reference[hundredPlace] != undefined) {
        convertedNum += reference[hundredPlace];
    };
    if (reference[tenPlace] != undefined) {
        convertedNum += reference[tenPlace];
    };
    if (reference[onePlace] != undefined) {
        convertedNum += reference[onePlace];
    };

 return convertedNum;
}

convertToRoman(400);
